import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { CartService } from 'src/app/services/cart.service';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {

  constructor(private authService: AuthService, private cartService: CartService, private productService: ProductService, private http: HttpClient) { }

  productsInCart: any = []

  private apiForPurchase = 'http://localhost:8000/api/payments/pay-with-stripe'

  ngOnInit(): void {
    this.getItemsInCart()
  }

  getItemsInCart() {
    this.cartService.getItemsOnCart().subscribe((DATA: any) => {
      console.log(DATA)
      let aver = []
      aver = DATA
      aver.forEach((element: any) => {
        const base64Image = this.productService.arrayBufferToBase64(element.imageData.data);
        element.imges = ['data:' + element.imageData.type + ';base64,' + base64Image];
        delete element.imageData; // Eliminar la propiedad imageData
      });
      this.productsInCart = DATA
    })
  }

