import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { CartService } from 'src/app/services/cart.service';
import { ProductService } from 'src/app/services/product.service';
import { NgxImageCompressService } from 'ngx-image-compress';
import { UsersService } from 'src/app/services/users.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {

  constructor(private userService: UsersService, private authService: AuthService, private cartService: CartService, private productService: ProductService, private http: HttpClient) { }

  productsInCart: any = []

  private apiForPurchase = 'http://localhost:8000/api/payments/pay-with-stripe'

  ngOnInit(): void {
    this.getItemsInCart()
    const user = localStorage.getItem("username")

    console.log(user)
  }

  getItemsInCart() {
    this.cartService.getItemsOnCart().subscribe((DATA: any) => {
      console.log(DATA);
      this.productsInCart = DATA.map((element: any) => {

        return {
          ...element,
          imgUrl: this.getProductImageUrl(element.imageData.data, element.imageData.type)
        };
      });
    });
  }

  getProductImageUrl(data: ArrayBuffer, type: string): string {
    const base64Image = this.productService.arrayBufferToBase64(data);
    return 'data:' + type + ';base64,' + base64Image;
  }

  deleteFromCart(product: any) {
    console.log(product.id)
    let id: string = product.id
    this.cartService.deleteItemInCart(id)
  }

  async payWithStripe() {

    let cart = JSON.parse(JSON.stringify(this.productsInCart));
    for (let i = 0; i < cart.length; i++) {
        delete cart[i].imageData 
        delete cart[i].imgUrl;
    }

    console.log(cart);

    const refreshToken = this.authService.getRefreshToken();
    const username = localStorage.getItem("username")
    console.log(username)

    const storedJsonString = localStorage.getItem('userInfo');
    if (storedJsonString) {
      const storedData = JSON.parse(storedJsonString);
      console.log(storedData.email); // Output: value1
      console.log(storedData.username); // Output: value2
      console.log(storedData.firstName); // Output: value2
      console.log(storedData.lastName); // Output: value2

      // cart.firstName = storedData.firstName
      // cart.lastName = storedData.lastName
      // cart.email = storedData.email
      // cart.username = storedData.username
      const cartWithUserInfo = {
        cart: cart,
        firstName: storedData.firstName,
        lastName: storedData.lastName,
        email: storedData.email,
        username: storedData.username
      };


      if (refreshToken) {
        const headers = new HttpHeaders({
          'Authorization': `Bearer ${refreshToken}`
        });

        return this.http.post<any>(this.apiForPurchase, { cartWithUserInfo }, { headers }).subscribe(resp => {
          const sessionUrl = resp.sessionUrl;
          window.location.href = sessionUrl;
        })

      }


    } else {
      console.log('No hay datos almacenados en localStorage.');
    }





    return null
  }


}


<div class="container col 12">

</div>


<div class="container col-6">
  <div *ngFor="let product of productsInCart; let i = index">
    <div class="card" style="width: 18rem;">
      <img [src]="product.imgUrl" style="width: 300px; height: 350px;" alt="Product Image">
      <button href="#" class="btn btn-danger" (click)="deleteFromCart(product)">ELIMINAR</button>

      <div class="card-body">
        <h5 class="card-title">{{ product.name }}</h5>
        <p class="card-text">{{ product.description }}</p>
      </div>
    </div>
  </div>

  <div class="payment-section">
    <button class="btn btn-primary" (click)="payWithStripe()">PAGAR</button>
  </div>
</div>

<div class="container col-6" style="border: 1px solid #ddd; padding: 20px; border-radius: 8px; background-color: #f9f9f9;">
  <h2 style="margin-bottom: 10px;">Resumen de Compra</h2>
  <p>Total de Compra: $100.00</p>

  <form>
    <label for="direccion">Dirección de Envío:</label>
    <input type="text" id="direccion" name="direccion" style="width: 100%; padding: 8px; margin-bottom: 10px; border: 1px solid #ccc; border-radius: 4px;">

    <label for="ciudad">Ciudad:</label>
    <input type="text" id="ciudad" name="ciudad" style="width: 100%; padding: 8px; margin-bottom: 10px; border: 1px solid #ccc; border-radius: 4px;">

    <label for="codigoPostal">Código Postal:</label>
    <input type="text" id="codigoPostal" name="codigoPostal" style="width: 100%; padding: 8px; margin-bottom: 10px; border: 1px solid #ccc; border-radius: 4px;">

    <button type="submit" style="background-color: #007bff; color: white; padding: 8px 16px; border: none; border-radius: 4px; cursor: pointer;">Enviar Pedido</button>
  </form>
</div>
