import { Component } from '@angular/core';
import { ProductService } from '../services/product.service';
import { ProductInterface } from '../interfaces/product.interface';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-admin-panel',
  templateUrl: './admin-panel.component.html',
  styleUrls: ['./admin-panel.component.css']
})
export class AdminPanelComponent {

  response: any;
  imgs: any[] = [];

  product: ProductInterface = {
    productName: '',
    description: '',
    totalAmount: 0,
    quantity: 0
  };

  selectedFile: File | null = null;
  private productAddedSubscription: Subscription;

  constructor(private productService: ProductService) {
    this.getData();
    // Suscribirse a la notificación de productos agregados o eliminados
    this.productAddedSubscription = this.productService.onProductChange().subscribe(() => {
      this.getData();
    });
  }

  deleteItem(i: any) {
    this.productService.deleteItem(i)
  }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }
  getData() {
    this.imgs = [];
    this.productService.getProducts().subscribe((reso: any) => {
      console.log(reso);
      this.response = reso;
      reso.forEach((element: any) => {
        const base64Image = this.productService.arrayBufferToBase64(element.imageData.data);
        element.imges = ['data:' + element.imageData.type + ';base64,' + base64Image];
      });
    });
  }

  onSubmit(form: any) {
    if (form.valid && this.selectedFile) {
      this.productService.addProduct(this.product, this.selectedFile)
        .subscribe(response => {
          console.log(response)
          form.reset();
          if (response) {
            // Response de exito es 'Producto agregado exitosamente:'
            console.log('Producto agregado exitosamente:', response);
          }
        });
    }
  }

  ngOnDestroy() {
    this.productAddedSubscription.unsubscribe();
  }

}
<div class="container mt-5">
  <h2>ADMIN PANEL</h2>
</div>

<div class="container mt-4">
  <h3>ADD PRODUCT</h3>
  <form #productForm="ngForm" (submit)="onSubmit(productForm)">
    <div class="mb-3">
      <label for="productName" class="form-label">Product Name</label>
      <input type="text" class="form-control" name="productName" [(ngModel)]="product.productName" required>
    </div>
    <div class="mb-3">
      <label for="description" class="form-label">Description</label>
      <input type="text" class="form-control" name="description" [(ngModel)]="product.description" required>
    </div>
    <div class="mb-3">
      <label for="totalAmount" class="form-label">Total Amount</label>
      <input type="number" class="form-control" name="totalAmount" [(ngModel)]="product.totalAmount" required>
    </div>
    <div class="mb-3">
      <label for="quantity" class="form-label">Quantity</label>
      <input type="number" class="form-control" name="quantity" [(ngModel)]="product.quantity" required>
    </div>
    <div class="mb-3">
      <label for="image" class="form-label">Product Image</label>
      <input type="file" class="form-control" (change)="onFileSelected($event)" accept="image/*" required>
    </div>
    <button type="submit" class="btn btn-primary">Enviar</button>
  </form>
</div>

<div class="container mt-5">
  <h3>PRODUCT LIST</h3>
  <div class="row">
    <div *ngFor="let img of response; let i = index" class="col-md-4 mb-4">
      <div class="card">
        <img [src]="img.imges[0]" class="card-img-top img-fluid" alt="Product Image">
        <div class="card-body">
          <h5 class="card-title">Product Name</h5>
          <p class="card-text">{{ img.description }}</p>
          <button class="btn btn-danger" (click)="deleteItem(img)">ELIMINAR</button>
        </div>
      </div>
    </div>
  </div>
</div>
