import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { CartService } from 'src/app/services/cart.service';
import { ProductService } from 'src/app/services/product.service';
import { NgxImageCompressService } from 'ngx-image-compress';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {

  constructor(private imageCompress: NgxImageCompressService, private authService: AuthService, private cartService: CartService, private productService: ProductService, private http: HttpClient) { }

  productsInCart: any = []

  private apiForPurchase = 'http://localhost:8000/api/payments/pay-with-stripe'

  ngOnInit(): void {
    this.getItemsInCart()
    const user = localStorage.getItem("username")
    localStorage.removeItem("user");

    console.log(user)
  }

  getItemsInCart() {
    this.cartService.getItemsOnCart().subscribe((DATA: any) => {
      console.log(DATA);
      this.productsInCart = DATA.map((element: any) => {

        return {
          ...element,
          imgUrl: this.getProductImageUrl(element.imageData.data, element.imageData.type)
        };
      });
    });
  }

  getProductImageUrl(data: ArrayBuffer, type: string): string {
    const base64Image = this.productService.arrayBufferToBase64(data);
    return 'data:' + type + ';base64,' + base64Image;
  }

  deleteFromCart(product: any) {
    console.log(product.id)
    let id: string = product.id
    this.cartService.deleteItemInCart(id)
  }

  async payWithStripe() {

    let cart = JSON.parse(JSON.stringify(this.productsInCart));
    for (let i = 0; i < cart.length; i++) {
        delete cart[i].imageData 
        delete cart[i].imgUrl;
    }

    console.log(cart);

    const refreshToken = this.authService.getRefreshToken();
    const user = localStorage.getItem("user")
    if (refreshToken) {
      const headers = new HttpHeaders({
        'Authorization': `Bearer ${refreshToken}`
      });

      return this.http.post<any>(this.apiForPurchase, { cart }, { headers }).subscribe(resp => {
        const sessionUrl = resp.sessionUrl;
        window.location.href = sessionUrl;
      })
    }
    return null
  }

  imgResultAfterCompression: string = '';
  imgResultBeforeCompression: string = '';

  async compressFile(img: any): Promise<any> {
    try {
      const compressedImage = await this.imageCompress
        .compressFile(img, 0, 20, 20 ); // 50% ratio, 50% quality
      console.log('Size in bytes after compression is now:', this.imageCompress.byteCount(compressedImage));
      return compressedImage;
    } catch (error) {
      console.error('Error al comprimir la imagen:', error);
      throw error;
    }
  }


}
