import { Component } from '@angular/core';
import { ApiService } from 'src/app/services/api.service';
import { AuthService } from 'src/app/services/auth.service';
import { CartService } from 'src/app/services/cart.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  constructor(private cartService: CartService, private authService: AuthService, private cdr: ChangeDetectorRef) {}

  user: string | null = '';
  shoppingCartCount: number = 0;
  shoppingCartTotal: number = 0;

  ngOnInit() {
    this.getItems();
    this.getItemsOnShoppingCart()
    this.getUsername()
  }

  getUsername(){
    this.user = this.authService.getUsername()
  }

  getItemsOnShoppingCart(){
    this.cartService.getItemsOnCart().subscribe(DATA=>{
      console.log(DATA)
    })
  }

  getItems() {
    this.cartService.shoppingCart$.subscribe(data => {
      this.shoppingCartCount = data.shoppingCart_items.length
    })
    this.cartService.shoppingCartAmount$.subscribe(resp=>{
      console.log(resp)
      this.shoppingCartTotal = resp
    })
  }

}
