import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { AuthService } from './auth.service';

@Injectable({
  providedIn: 'root'
})
export class UsersService {

  constructor(private readonly http: HttpClient, private authService: AuthService) { }

  private apiUrl = 'http://localhost:8000/api/users/all';


  getUsers() {

    const refreshToken = this.authService.getAccessToken();
    if (refreshToken) {
      const headers = new HttpHeaders({
        'Authorization': `Bearer ${refreshToken}`
      });


    return this.http.get(this.apiUrl).subscribe(data=>{
      return data
    })
  }
}
}
